import React from 'react';
import { 
    Copyright,
    Up,
    UpIcon
} from './Footer.styles';

const Footer = () => {

    const scrollTop = () =>{
        window.scrollTo({top: 0, behavior: 'smooth'});
     };

    return (
        <Copyright>
		    <Up onClick={scrollTop} id="up">
			    <i className="fa fa-arrow-up"></i>
		    </Up>
		    <p>&copy; 2021 Megan Chong</p>
	    </Copyright>
    );
}

export default Footer;
