import styled from 'styled-components';

const PortfolioItemContainer = styled.div`
    display: flex;
	align-items: center;
	justify-content: center;
	margin-bottom: 5rem;
    &:last-child {
        margin-bottom: 0;
    }
`

const PortfolioDescriptionContainer = styled.div`
    margin-right: 5rem;
`

export { PortfolioItemContainer
 };
