import styled from 'styled-components';

const HeaderContainer = styled.header`
    width: 100%;
    height: ${(props) => props.height};
`

const NavList = styled.ul`
    list-style: none;
	width: 100%;
	height: 100%;
	display: flex;
	justify-content: center;
	align-items: center;
`

const NavLink = styled.link`
    font-family: fieldwork;
	color: #ffffff;
	font-size: 5rem;
	padding: .5rem;

    :hover, : {

    }
`

export { 
    HeaderContainer,
    NavList,
    NavLink
}
